CREATE SCHEMA  `twitter_database`;

CREATE TABLE `twitter_database`.`user_table` (
`user_id` INT NOT NULL,
`name` VARCHAR(45) NOT NULL,
`lastname` VARCHAR(45) NULL,
`email` VARCHAR(45) NOT NULL,
`password` VARCHAR(45) NOT NULL,
PRIMARY KEY (`user_id`));



CREATE TABLE `twitter_database`.`followers` (
`user_id` INT NOT NULL,
`name` VARCHAR(45) NOT NULL,
`lastname` VARCHAR(45) NULL,
`email` VARCHAR(45) NOT NULL,
`password` VARCHAR(45) NOT NULL,
PRIMARY KEY (`user_id`),
FOREIGN KEY (user_id) REFERENCES user_table(user_id));



CREATE TABLE `twitter_database`.`feeds` (
`Feed_id` INT NOT NULL,
`User_id` INT NOT NULL,
`Feed_content` VARCHAR(45) NULL,
PRIMARY KEY (`Feed_id`),
FOREIGN KEY (User_id) REFERENCES user_table(user_id));



CREATE TABLE `twitter_database`.`retweets` (
`Retweet_id` INT NOT NULL,
`Feed_id` INT NOT NULL,
`User_id` INT NOT NULL,
PRIMARY KEY (`Retweet_id`),
FOREIGN KEY (User_id) REFERENCES user_table(user_id),
FOREIGN KEY (Feed_id) REFERENCES feeds(Feed_id)
);



CREATE TABLE `twitter_database`.`comments` (
`Comment_id` INT NOT NULL,
`Comment_content` LONGTEXT NULL,
`Feed_id` INT NOT NULL,
`User_id` INT NOT NULL,
PRIMARY KEY (`Comment_id`),
FOREIGN KEY (User_id) REFERENCES user_table(user_id),
FOREIGN KEY (Feed_id) REFERENCES feeds(Feed_id));


